import './App.css';
import { Component } from 'react';

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: '', // State to hold the textarea value
    };
  }

  handleButtonClick = (char) => {
    this.setState((prevState) => ({
      text: prevState.text + char, // Append character to state
    }));
  };

  handleDelete = () => {
    this.setState((prevState) => ({
      text: prevState.text.slice(0, -1), // Remove last character
    }));
  };

  render() {
    return (
      <div className="container">
        <div className="textarea">
          <textarea value={this.state.text} readOnly></textarea>
        </div>
        <div className="keyboard">
          <div className="keyboardRow">
            {'QWERTYUIOP'.split('').map((char) => (
              <button
                key={char}
                className="button characterButton"
                onClick={() => this.handleButtonClick(char)}
              >
                {char}
              </button>
            ))}
          </div>
          <div className="keyboardRow">
            <div className="keyboardSpacer"></div>
            {'ASDFGHJKL'.split('').map((char) => (
              <button
                key={char}
                className="button characterButton"
                onClick={() => this.handleButtonClick(char)}
              >
                {char}
              </button>
            ))}
            <div className="keyboardSpacer"></div>
          </div>
          <div className="keyboardRow">
            <button className="button enterButton">Enter</button>
            {'ZXCVBNM'.split('').map((char) => (
              <button
                key={char}
                className="button characterButton"
                onClick={() => this.handleButtonClick(char)}
              >
                {char}
              </button>
            ))}
            <button className="button deleteButton" onClick={this.handleDelete}>
              <img
                src="https://static-00.iconduck.com/assets.00/backspace-icon-2048x1509-3pqr8k29.png"
                alt="backspace"
              />
            </button>
          </div>
        </div>
      </div>
    );
  }
}

function App() {
  return (
    <div>
      <Header />
    </div>
  );
}

export default App;
